import React, { useState } from 'react'
import Postit from '../UI/Postit'

const Home = () => {

  const [item, setItem] = useState("Clique para começar!");

  const chordsList = [
    "C",
    "D",
    "E",
    "F",
    "G",
    "A",
    "B",
    "C#m",
    "F#m",
    "G#m",
    "Cm",
    "Dm",
    "Em",
    "Fm",
    "Gm",
    "Am",
    "Bm",
  ];

  const choseChord = () => {
    setItem((prev) => {
      let index = Math.floor(Math.random() * chordsList.length)
      console.log(index)
      if(prev === index && !prev > 16){
        index += 1
      } else {
        index -= 1
      }
      return index
    })
  }

  return (
    <Postit onClick={choseChord} content={item}/>
  )
}

export default Home
