import React, { useState } from 'react'
import Postit from '../UI/Postit'

const Home = () => {

  const [item, setItem] = useState("Clique para começar!");

  const chordsList = [
    "C",
    "D",
    "E",
    "F",
    "G",
    "A",
    "B",
    "C#m",
    "F#m",
    "G#m",
    "Cm",
    "Dm",
    "Em",
    "Fm",
    "Gm",
    "Am",
    "Bm",
  ];

  const choseChord = () => {
    setItem(chordsList[Math.floor(Math.random() * chordsList.length)])
  }

  return (
    <>
    {item === "Clique para começar!" && <h1>{item}</h1> && <Postit onClick={choseChord} content={item}/>}
    </>
  )
}

export default Home
