    // props.onSetcale()
  };

  return (
    <div className={styles.main}>
      <h1 className={styles.title}>Random Chord ðŸŽµ</h1>
      <p className={styles.par}>Escolha uma escala para praticar.</p>
      { scalesData.length === 0  && <p>Loading ...</p> }
      { scalesData.map(scale => {
          return <Button key={scale.id} onClick={(scale) => {sendScale(scale.id)}}>{scale.id} Major</Button>
        })
      }
    </div>
  );
};

export default HomePage;
